package com.day_06;

public class ArithOpers_4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		System.out.println(10/3);//3 division = quotient here in java , int/int = int
		System.out.println((float) 10/3); //float less precision

		System.out.println(10f/3); //float less precision

		System.out.println(10.0/3); //double with more precision
		
		
		System.out.println(10%3); // modulus = percentage =remainder =1
		
		System.out.println((5*6%4)); //rem =2
	}

}
